import json

def slugify(text):
    return text.lower().replace(' ', '-').replace("'", "").replace("&", "and").replace("(", "").replace(")", "").replace(",", "").replace(".", "").replace(";", "")

themes_by_topic = {}
theme_subtopic_mapping = {}

lines = data.strip().split('\n')
for line in lines:
    parts = line.split('\t')
    if len(parts) < 4:
        continue
    
    theme_name = parts[0].strip()
    subtopics_str = parts[1].strip()
    topic_theme_name = parts[2].strip()
    emoji = parts[3].strip()
    
    theme_id = slugify(theme_name)
    topic_id = slugify(topic_theme_name)
    
    if topic_id not in themes_by_topic:
        themes_by_topic[topic_id] = []
    
    subtopics = [s.strip() for s in subtopics_str.split(',') if s.strip()]
    
    themes_by_topic[topic_id].append({
        "id": theme_id,
        "name": theme_name,
        "description": f"Real-world applications of mathematics in the context of {theme_name.lower()}.",
        "tags": ["general"],
        "topicId": topic_id,
        "emoji": emoji,
        "subtopicIds": subtopics # Store actual subtopic names for now
    })
    
    theme_subtopic_mapping[theme_id] = subtopics

print(json.dumps({
    "themes_by_topic": themes_by_topic,
    "theme_subtopic_mapping": theme_subtopic_mapping
}, indent=2))
